mapper.select:
select

from news
where news_title = #{newsTitle}
and id <>  #{id}

mapper.insert:
insert into news (create_user_id, news_title,
  news_content, create_date, news_status
  )
values (#{sysuser.id}, #{newsTitle},
  #{newsContent}, #{createDate}, #{newsStatus}
  )

mapper.update:
update news
      set news_status=#{newsStatus}
      where id=#{id}
